@@ -793,6 +793,11 @@ bool WebContents::IsLoading() const {
   return web_contents()->IsLoading();
 }

+bool WebContents::IsLoadingToDifferentDocument() const {
+  return (web_contents()->GetSiteInstance() !=
+          web_contents()->GetPendingSiteInstance()) && IsLoading();
+}
